using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class enemyHitTarget : MonoBehaviour {    //Should be placed on target object for dijkstra map.    public GameObject LevelController;    LevelMaster levelScript;    	void Start () {        levelScript = LevelController.GetComponent<LevelMaster>();	}    private void OnTriggerEnter2D(Collider2D collider)    {        if(collider.gameObject.tag == "Monster")        {            //Despawns an enemy and updates information in levelMaster.            levelScript.despawnEnemy(collider.transform.parent.GetComponent<Monster>().damage, collider.transform.parent.GetComponent<Monster>().points, true);            Destroy(collider.gameObject.transform.parent.gameObject);        }    }}